<script setup>
import vdFondo from "@/assets/video/vdFondoVeterinaria.mp4";
import { VToast } from "@/views/components";
import axios from "@axios";
import { themeConfig } from "@themeConfig";
import { onMounted } from "vue";

const selectedTipo = ref("username")
const valorCampo = ref("")
const loadings = ref([])
const isActive = ref(false)
const title = ref("")
const message = ref("")
const router = useRouter();

const tipo = ref([
    {
        "key": "username",
        "name": "Usuario",
        "icon": "tabler-user"
    },
    {
        "key": "document_number",
        "name": "DNI",
        "icon": "tabler-signature"
    },
    {
        "key": "email",
        "name": "Correo",
        "icon": "tabler-mail"
    }
])
const valueToas = ref({});
const timer = ref(null);


const recuperarPassword = async () => {
    loadings.value[0] = true

    if (valorCampo.value == '') {
        loadings.value[0] = false
        return false
    }

    var data = {
        "campo": selectedTipo.value,
        "value": valorCampo.value
    }

    await axios({
        url: "backend-citas/reset-password/",
        method: "POST",
        data: data
    }).then(res => {
        if (!res.data.is_error) {
            isActive.value = true
            title.value = "Correo Enviado"
            message.value = res.data.message

            setTimeout(() => {
                router.replace({ path: `/reset-password/${res.data.token}` });
            }, 4000);
        } else {
            valueToas.value = {
                status: true,
                title: "Operación sin exito!",
                menssage: res.data.message,
                type: "error",
            };
        }
    }).catch(err => {
        valueToas.value = {
            status: true,
            title: "Operación sin exito!",
            menssage: err,
            type: "error",
        };
    })
    eventMause();
    loadings.value[0] = false
}
onMounted(() => {
})
function closeToast() {
    valueToas.value = {
        status: false,
    };
}

function eventMause() {
    timer.value = setTimeout(() => closeToast(), 5000);
}

function eventMouseover() {
    clearTimeout(timer.value);
}
</script>
